import React, { useState } from 'react';
import CryptoJS from 'crypto-js';

const PaymentForm = () => {
    const [amount, setAmount] = useState(100);
    const [taxAmount, setTaxAmount] = useState(10);
    const [totalAmount, setTotalAmount] = useState(110);
    const [transactionUuid] = useState('unique-transaction-id');
    const [error, setError] = useState('');

    const calculateAmounts = (amount) => {
        const tax = amount * 0.1;
        setTaxAmount(tax);
        setTotalAmount(amount + tax);
    };

    const generateSignature = (totalAmount, transactionUuid, productCode) => {
        const secretKey = '8gBm/:&EnhH.1/q('; // Use the provided secret key for UAT
        const message = `total_amount=${totalAmount},transaction_uuid=${transactionUuid},product_code=${productCode}`;
        const hash = CryptoJS.HmacSHA256(message, secretKey);
        return CryptoJS.enc.Base64.stringify(hash);
    };

    const handleSubmit = (event) => {
        try {
            const productCode = 'EPAYTEST';
            const signature = generateSignature(totalAmount, transactionUuid, productCode);
            document.getElementById('signature').value = signature;
        } catch (error) {
            console.error('Error in handleSubmit:', error);
            setError('An error occurred while generating the signature.');
            event.preventDefault();
        }
    };

    const handleAmountChange = (event) => {
        const newAmount = parseFloat(event.target.value) || 0;
        setAmount(newAmount);
        calculateAmounts(newAmount);
    };

    return (
        <div className="max-w-md mx-auto p-4 bg-white shadow-md rounded-md">
            {error && <p className="text-red-500">{error}</p>}
            <form action="https://rc-epay.esewa.com.np/api/epay/main/v2/form" method="POST" onSubmit={handleSubmit}>
                <div className="mb-4">
                    <label htmlFor="amount" className="block text-sm font-medium text-gray-700">Amount</label>
                    <input
                        type="number"
                        id="amount"
                        name="amount"
                        value={amount}
                        onChange={handleAmountChange}
                        required
                        className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                    />
                </div>
                <div className="mb-4">
                    <label htmlFor="tax_amount" className="block text-sm font-medium text-gray-700">Tax Amount</label>
                    <input
                        type="text"
                        id="tax_amount"
                        name="tax_amount"
                        value={taxAmount}
                        readOnly
                        required
                        className="mt-1 block w-full p-2 border border-gray-300 rounded-md bg-gray-100"
                    />
                </div>
                <div className="mb-4">
                    <label htmlFor="total_amount" className="block text-sm font-medium text-gray-700">Total Amount</label>
                    <input
                        type="text"
                        id="total_amount"
                        name="total_amount"
                        value={totalAmount}
                        readOnly
                        required
                        className="mt-1 block w-full p-2 border border-gray-300 rounded-md bg-gray-100"
                    />
                </div>
                <div className="mb-4">
                    <label htmlFor="esewa_id" className="block text-sm font-medium text-gray-700">eSewa ID</label>
                    <input
                        type="text"
                        id="esewa_id"
                        name="esewa_id"
                        required
                        className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                    />
                </div>
                <div className="mb-4">
                    <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        required
                        className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                    />
                </div>
                <input type="hidden" id="transaction_uuid" name="transaction_uuid" value={transactionUuid} />
                <input type="hidden" id="product_code" name="product_code" value="EPAYTEST" />
                <input type="hidden" id="product_service_charge" name="product_service_charge" value="0" />
                <input type="hidden" id="product_delivery_charge" name="product_delivery_charge" value="0" />
                <input type="hidden" id="success_url" name="success_url" value="http://localhost:3000/success" />
                <input type="hidden" id="failure_url" name="failure_url" value="http://localhost:3000/failure" />
                <input type="hidden" id="signed_field_names" name="signed_field_names" value="total_amount,transaction_uuid,product_code" />
                <input type="hidden" id="signature" name="signature" value="" />
                <button type="submit" className="w-full bg-blue-500 text-white py-2 px-4 rounded-md">Submit</button>
            </form>
        </div>
    );
}

export default PaymentForm;
